package com.java.aug;

public class mixedoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double i = 3.0/2 ;    // this is implicit type casting result is 1.5
		
		 System.out.println("The Value of i = "+i);
		 //this is called casting.foe r this result will come in
		 //decimal value we call in java double.thats what we store it in double command.
		 //double is higher than the int
		 
		 		 
		 //we can write this way also
		 
		 double d = (double)3/2;  //Explict type casting
		 System.out.println("The Value of d = "+d);
		  
	}
	}

